package lib;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class DatabaseConnection {    private static final String JDBC_URL = "jdbc:postgresql://localhost:5432/stocks_management_db";    private static final String USERNAME = "postgres";    private static final String PASSWORD = "ryta123";    static {        try {            Class.forName("org.postgresql.Driver");        } catch (ClassNotFoundException e) {            e.printStackTrace();            throw new RuntimeException("Error loading PostgreSQL JDBC Driver");        }    }    public static Connection getConnection() throws SQLException {        return DriverManager.getConnection(JDBC_URL, USERNAME, PASSWORD);    }    public static void main(String[] args) {    }}